import sys
from os import path
DIR = path.dirname(__file__)
sys.path.append(DIR)

from TopLevelError import TopLevelError
from apps.AppManager import runApps

def main():
	args = sys.argv[1:]
	try:
		out = runApps(args)
	except TopLevelError as e:
		sys.stderr.write(e.what + "\n")
		raise SystemExit(1)
