from ..BaseApp import BaseApp
from project.ConfigFile import ConfigFile
from Config import INSTALL_INI, KEY_REQUIRE
from tools.install import install

class App(BaseApp):
	"Setup of the package in the current directory"
	
	def main(self):
		config = ConfigFile(INSTALL_INI)
		requires = config.get(KEY_REQUIRE)
		install(requires)
	
	def options(self):
		return ["--setup"]
