from .ProcessReport import ProcessReport

class Command:
	
	def __init__(self, cmdString):
		self.cmd = cmdString
	
	def run(self, **kwargs):
		report = self.makeProcessReport(kwargs)
		report.obtainResults()
		report.checkError()
		return report

	def runAsync(self, **kwargs):
		return self.makeProcessReport(kwargs)
	
	# ========== PRIVATE HELPERS ==============================
	
	def makeProcessReport(self, kwargs):
		cmd = self.cmd.format(**kwargs)
		return ProcessReport(cmd)
