from .uninstall import uninstalls
from datafile.InstalledLog import InstalledLog
from Config import INSTALLED_F

NO_ACTION = "No packages are installed so none were uninstalled."

def removeall():
	with InstalledLog(INSTALLED_F) as log:
		pkgs = log.show()
		if not pkgs:
			print(NO_ACTION)
		else:
			uninstalls(pkgs, log)
